import React, { useState } from 'react'
import Product from '../../components/tile/Product'
import './ProductPage.css'

function ProductPage() {

    const [ product, setProduct] = useState([])
    const [ name, setName] = useState('')
    const [ specification, setSpecification] = useState('')
    const [ price, setPrice] = useState(0)
    cons

    function handleName(e){
        setName(e.target.value)
    }

    function handleSpecification(e){
        setSpecification(e.target.value)
    }

    function handlePrice(e){
        setPrice(e.target.value)
    }

    function handleProduct(){
        setProduct((val)=>{
            return [...val,{id: val.length + 1, name: name, specification: specification, price: price}]})

        setName('')
        setPrice()
        setSpecification('')
    }

    function handleDeleteProduct(e){
        setProduct((val)=>{
            return val.filter((product, index)=> index !== e)
        })
    }



    return(
        <div className='App'>
            <div className='product-container'>
                {
                    product.map((pro, index)=>{
                        return <Product key={index} name={pro.name} specification={pro.specification} price={pro.price} deleteProduct={handleDeleteProduct} index={index}></Product>
                    })
                }
            </div>  
            <div className='add-container'>
                <div className='row'>
                    <input type='text' value={name} onChange={handleName} placeholder='Name'></input>
                    <input type='text' value={specification} onChange={handleSpecification} placeholder='Specification'></input>
                    <input type='text' value={price} onChange={handlePrice} placeholder='Price'></input>
                    <button type='submit' onClick={handleProduct}>ADD</button>
                </div>
            </div>   
        </div>

    );
};
export default ProductPage
